import React from "react";

function Popup(props) {
    const [close, setClose] = React.useState(props.trigger);

    function insidePopup() {
        return (
            <div className="popup">
                <button type="button" onClick={() => setClose(false)}>Close</button>
            </div>
        )
    }
    return (
        <div>
            {props.trigger === true ? insidePopup() : null}
        </div>
    );
}

export default Popup;
