import React,{useEffect} from "react";
import ReactDOM from "react-dom";
import Navbar from "./NavBar";
import Search from "./Search";

function HomePage() {
    const [errorName, setErrorName] = React.useState([]);
    const [errorContent, setErrorContent] = React.useState([]);
    const [errorDate, setErrorDate] = React.useState([]);
    const [errorList, setErrorList] = React.useState([]);

    useEffect(() => {
        // Update the document title using the browser API
      },[]);
    
    async function getError() {
        const response = await fetch("http://localhost:5000/error");
        const jsonData = await response.json();
        debugger;
        setErrorList(jsonData);
        // jsonData.map(getElements);
        // getTableContents();
    }

    function getElements(index) {
        setErrorName([...errorName, index.errorName]);
        setErrorContent([...errorContent, index.errorContent]);
        setErrorDate([...errorDate, index.errorDate]);
        debugger;
    }

    function printElements(e) {
        return e;
    }

    function getTableContents() {
        return (
               
<div>
                    {errorList.map(x=>{
                        <tr>
  <td><center>{x.errorName}</center></td>
  {/* <td><center>{errorContent.map(printElements)}</center></td>
  <td><center>{errorDate.map(printElements)}</center></td> */}
    </tr>
                    })

                    }
                  
                  </div> 
        )
    }

    return (
        <div>
            <Navbar></Navbar>
            <Search></Search><br></br>
            <center><button type="button" onClick={getError}>List All Errors</button></center>
            <br></br>
            <center>
                <table className="error-table">
                    <tr className="table-tr">
                        <th className="table-td">Date</th>
                        <th className="table-td">Error Header</th>
                        <th className="table-td">Error</th>
                    </tr>
                    {getTableContents()}  

                </table>
            </center>
        </div>
    );

}

export default HomePage;
