import React from "react";

function CommentPopup(props) {

    function insidePopup() {
        return (
            <div className="popup">
                <div className="popup-inner2">
                    <br></br>
                    <input type="textarea" style={{width: "400px", height: "120px"}} className="form-control" id="floatingInput" step="any"/>
                    <button type="button" className="btn btn-success">Add</button>&nbsp;&nbsp;&nbsp;
                    <button type="button" onClick={() => props.setTrigger(false)} className="btn btn-danger">Close</button>
                </div>
            </div>
        )
    }

    return (
        <div>
            {props.openPopup ? insidePopup() : null}
        </div>
    )
}

export default CommentPopup;
