import React from "react";
import ReactDOM from "react-dom";
import {IoIosPerson} from "react-icons/io";
import {AiFillMail} from "react-icons/ai";
import {RiLockPasswordFill} from "react-icons/ri";
import {Link} from "react-router-dom";

function SignInPage() {
    const [email, setEmail] = React.useState("");
    const [password, setPassword] = React.useState("");

    async function checkLogin() {
        console.log("aaaaa");
        const response = await fetch("http://localhost:5000/todo");
        debugger
        console.log(response);

    }

    return (
        <div>
            <form onSubmit={checkLogin}>
                <center>
                    <div className="mb-3 mt-5">
                        <h2 className="jumbotron">Sign In</h2>
                        <table>
                            <tr><td><AiFillMail></AiFillMail></td><td><input type="surname" onChange={e => setEmail(e.target.value)} className="form-control" id="floatingInput" placeholder="Email" style={{margin: 10}}/></td></tr>
                            <tr><td><RiLockPasswordFill></RiLockPasswordFill></td><td><input type="password" onChange={e => setPassword(e.target.value)} className="form-control" id="floatingInput" placeholder="Password" style={{margin: 10}}/></td></tr>
                        </table>
                        <br></br>
                        <button type="submit" className="btn btn-success">Log In</button><br></br><br></br>
                        <p style={{color: "DarkGrey"}}>Not registered? <Link to="RegistrationPage">Create an account</Link></p>
                    </div>
                </center>
            </form>
        </div>
    );
}

export default SignInPage;
