import React, { useEffect } from "react";
import NavBar from "./NavBar";
import {Cookies} from "react-cookie"
function ProfilePage() {
    const cookies = new Cookies();
    const userId = cookies.get("userId");
    const [name, setName] = React.useState("");
    const [errors, setErrors] = React.useState([]);

    useEffect(() => {
        getName();
        getErrors();
      }, [])


    async function getName() {
        debugger;
        const response = await fetch("http://localhost:5000/profile/" + userId);
        const jsonData = await response.json();
        setName(jsonData[0].name);
    }

    async function getErrors() {
        debugger;
        const response = await fetch("http://localhost:5000/profileErrors/" + userId);
        const jsonData = await response.json();
        setErrors(jsonData);
    }

    function printErrors(x) {
        return (
            <div>
                <tr className="table-tr">
                    <td className="table-td">{x.errorName}</td>
                    <td className="table-td">{x.errorContent}</td>
                </tr>
            </div>
        )
    }

    return (
        <div>
            <NavBar></NavBar><br></br>
            <center>
                <h1>Hello {name}</h1>
                <u><h3>Your Errors:</h3></u><br></br>
                <table className="error-table">
                    <tr className="table-tr">
                        <td className="table-td"><strong>Error Name</strong></td>
                        <td><strong>Error Content</strong></td>
                    </tr>
                    {errors.map(printErrors)}
                </table>
            </center>
        </div>
    )
}

export default ProfilePage;
