import React from "react";
import NavBar from "./NavBar";
import { Cookies } from "react-cookie";

function DetailsPage() {
    const cookies = new Cookies();
    const errorId = cookies.get("errorId");
    const [errorDetails, setErrorDetails] = React.useState([]);

    async function showDetails() {
        const response = await fetch("http://localhost:5000/anError/" + errorId);
        if (response.status == 200) {
            debugger;
            const jsonData = await response.json();
            setErrorDetails(jsonData);
        }
        return (
            <center>
                <h1 className="mt-5">{errorDetails.errorName}</h1>
                <h3>Publish Date:&nbsp;{errorDetails.errorDate}</h3><hr style={{width:"800px", borderWidth:"10px", border:"3px solid red"}}></hr>
                <p>{errorDetails.errorContent}</p>
            </center>
        )
    }

    return (
        <div>
            <NavBar></NavBar>
            {showDetails()}
        </div>
    );

}

export default DetailsPage;
