import React from "react";

function Popup(props) {
    const [close, setClose] = React.useState(props.trigger);

    function insidePopup() {
        return (
            <div className="popup">
                <div className="popup-inner">
                    <button type="button" className="btn btn-success popup-close-button" onClick={() => setClose(false)}>Close</button>
                </div>
            </div>
        )
    }
    return (
        <div>
            {close === true ? insidePopup() : null}
        </div>
    );
}

export default Popup;
