import React from "react";

function Popup(props) {
    function insidePopup() {
        return (
            <div className="popup">
                <div className="popup-inner">
                    <table>
                        <tr>
                            <td><input type="textarea" style={{width: "400px", height: "120px"}} class="form-control" id="floatingInput" step="any" placeholder="Enter the error header..."/></td>
                            <td><input type="textarea" style={{width: "400px", height: "120px"}} class="form-control" step="any" id="floatingPassword" placeholder="Enter the error content..."/></td>
                        </tr>
                    </table>
                    <br></br>
                    <button type="button" className="btn btn-success">Save</button>
                    <button type="button" className="btn btn-danger popup-close-button" onClick={() => props.setTrigger(false)}>Close</button>
                </div>
            </div>
        )
    }
    return (
        <div>
            {props.trigger === true ? insidePopup() : null}
        </div>
    );
}

export default Popup;
