import React from "react";
import NavBar from "./NavBar";
import { Cookies } from "react-cookie";

function DetailsPage() {
    const cookies = new Cookies();
    const errorId = cookies.get("errorId");
    const [errorDetails, setErrorDetails] = React.useState([]);
    debugger;
    const response = fetch("http://localhost:5000/anError/" + errorId);
    response.then(async response => {
        if(response.status == 200) {
            const value = await response.json();
            setErrorDetails(value);
        }
    });

    var x = new Date(errorDetails[0].errorDate);
    var date = x.getDay() + "-" + (x.getMonth() + 1) + "-" + x.getFullYear();

    return (
        <div>
            <NavBar></NavBar>
            <center>
                <h1 className="mt-5">{errorDetails[0].errorName}</h1>
                <h3>Publish Date:&nbsp;{date}</h3><hr style={{width:"800px", borderWidth:"10px", border:"3px solid red"}}></hr>
                <p>{errorDetails[0].errorContent}</p>
            </center>

        </div>
    );

}

export default DetailsPage;
