        const response = await fetch("http://localhost:5000/childComments/" + errorId + "/" + commentId);
    }

    function insidePopup() {
        return (
            <div className="popup">
                <div className="popup-inner2">
                    <br></br>
                    {getAllComments() ? 
                    <div>
                        <table style={{maxWidth:"1200px"}}>
                            {allComments.map(printOneByOne)}
                        </table>
                    </div> : null}
                    <button type="button" onClick={() => props.closePopup(false)} className="btn btn-danger">Close</button>
                </div>
            </div>
        )
    }

    return (
        <div>
            {props.openPopup ? insidePopup() : null}
        </div>
    )
}

export default CommentResultPopup;
