import React from "react";

function DetailsPopup(props) {

    async function deleteItem() {
        const temp = props.deleteId;
        const response = await fetch('http://localhost:5000/delete/' + temp, {
            method: "DELETE",
            });
        if (response.ok) {
            if (props.onUpdated) {
                props.onUpdated();
            }
        }
    }

    function insidePopup() {
        return (
            <div className="popup">
                <div className="popup-inner2">
                    <br></br>
                    <input type="textarea" style={{width: "400px", height: "120px"}} className="form-control" id="floatingInput" step="any"></input><br></br>
                    <button type="button" className="btn btn-success mt-3">Add</button>&nbsp;&nbsp;&nbsp;&nbsp;
                    <button type="button" className="btn btn-danger mt-3" onClick={() => props.closePopup(false)}>Close</button>
                </div>
            </div>
        )
    }
    debugger;

    return (
        <div>
            {props.trigger === true ? insidePopup() : null}
        </div>
    );
}

export default DetailsPopup;
