import React from "react";
import ReactDOM from "react-dom";
import Navbar from "./NavBar";
import Search from "./Search";

function HomePage() {
    const [error, setError] = React.useState([]);
    
    async function getError() {
        const response = await fetch("http://localhost:5000/error");
        const jsonData = await response.json();
        jsonData.map(getElements);
        getTableContents();
    }

    function getElements(index) {
        setError([... error, {
            errorName: index.errorName,
            errorContent: index.errorContent,
            errorDate: index.errorDate
        }]);
    }

    function printElements(e) {
        return e;
    }

    function getTableContents() {
        return (
                <tr>
                    <td><center>{errorName.map(printElements)}</center></td>
                    <td><center>{errorContent.map(printElements)}</center></td>
                    <td><center>{errorDate.map(printElements)}</center></td>
                </tr>
        )
    }

    return (
        <div>
            <Navbar></Navbar>
            <Search></Search><br></br>
            <center><button type="button" onClick={getError}>List All Errors</button></center>
            <br></br>
            <center>
                <table className="error-table">
                    <tr className="table-tr">
                        <th className="table-td">Date</th>
                        <th className="table-td">Error Header</th>
                        <th className="table-td">Error</th>
                    </tr>
                    {getTableContents()}
                </table>
            </center>
        </div>
    );

}

export default HomePage;
