import React from "react";
import {AiOutlinePlusCircle} from "react-icons/ai";
import DetailsPopup from "./DetailsPopup"

function DetailsPopupButton() {
    const [popup, setPopup] = React.useState(false);
    debugger;
    function openPopup() {
        setPopup(true);
    }

    return (
        <div>
            <button type="button" onClick={openPopup}><AiOutlinePlusCircle size="3em" className="mt-3 btn-success"></AiOutlinePlusCircle></button>
            <DetailsPopup trigger={popup} closePopup={setPopup}></DetailsPopup>
        </div>
    )
}

export default DetailsPopupButton;
