import React from "react";
import NavBar from "./NavBar";
import { Cookies } from "react-cookie";

function DetailsPage() {
    const cookies = new Cookies();
    const errorId = cookies.get("errorId");
    const [errorName, setErrorName] = React.useState("");
    const [errorContent, setErrorContent] = React.useState("");
    const [errorDate, setErrorDate] = React.useState("");

    const response = fetch("http://localhost:5000/anError/" + errorId);
    response.then(async response => {
        if(response.status == 200) {
            const value = await response.json();
            setErrorName(value.errorName);
            setErrorContent(value.errorContent);
            setErrorDate(value.errorDate);
        }
    });

    var date = errorDate.getDate() + "-" + (errorDate.getMonth() + 1) + "-" + errorDate.getFullYear();

    return (
        <div>
            <NavBar></NavBar>
            <center>
                <h1 className="mt-5">{errorName}</h1>
                <h3>Publish Date:&nbsp;{date}</h3><hr style={{width:"800px", borderWidth:"10px", border:"3px solid red"}}></hr>
                <p>{errorContent}</p>
            </center>

        </div>
    );

}

export default DetailsPage;
